def winner(n, k, cards):
    least_common, least_common_count = cards[0][0], 1
    counts = {card: 0 for card in set(sum(cards, []))}
    for card in sum(cards, []):
        counts[card] += 1
        if counts[card] < least_common_count:
            least_common, least_common_count = card, counts[card]

    for i, card in enumerate(cards):
        for j in range(n):
            if card[j] == 'J':
                if j + 1 < n and card[j + 1] == 'J':
                    return j + 1
            else:
                if card.count(card[j]) > 1:
                    return i + 1
    return k if least_common_count > 1 else k

N, K = map(int, input().split())
cards = [input() for _ in range(N)]
print(winner(N, K, cards))
